import { JsonPipe, NgFor } from '@angular/common';
import { Component } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { Validators } from '@angular/forms';

@Component({
  selector: 'app-profileForm',
  templateUrl: './profileForm.component.html',
  styleUrls: ['./profileForm.component.css']
})
export class ProfileFormComponent {
  profileForm = this.formBuilder.group({
    firstName: ['', Validators.required],
    lastName: [''],
    address: this.formBuilder.group({
      street: [''],
      city: [''],
      state: [''],
      zip: [''],
    }),
    aliases: this.formBuilder.group({
      emergencyContact:['']
    }),
  });

  constructor(private formBuilder: FormBuilder) { }

 
  onSubmit() {
    // TODO: Use EventEmitter with form value
    console.warn(this.profileForm.value);
  }
}
<form [formGroup]="profileForm" (ngSubmit)="onSubmit()">
  <div class="row">
    <div class="col-md-4">
      <label for="first-name">First Name: </label>
      <input id="first-name" type="text" formControlName="firstName" required>

      <label for="last-name">Last Name: </label>
      <input id="last-name" type="text" formControlName="lastName">

      <label for="date-of-birth">Date of birth: </label>
      <input id="date-of-birth" type="datetime" formControlName="dateOfBirth">

      <label for="gender">Gender: </label>
      <input id="gender" type="text" formControlName="gender">

      <label for="phone-number">Phone number: </label>
      <input id="phone-number" type="text" formControlName="phoneNumber">

      <label for="email">Email: </label>
      <input id="email" type="email" formControlName="email">
    </div>

    <div formGroupName="address" class="col-md-4">
      <h2>Address</h2>

      <label for="street">Street: </label>
      <input id="street" type="text" formControlName="street">

      <label for="city">City: </label>
      <input id="city" type="text" formControlName="city">

      <label for="country">Country: </label>
      <input id="country" type="text" formControlName="country">

      <label for="zip">Zip Code: </label>
      <input id="zip" type="text" formControlName="zip">
    </div>

    <div formArrayName="emergencyContact">
      <h2>Emergency contact</h2>

      <label for="emergency-contact-name">Name:</label>
      <input id="emergency-contact-name" type="text" formControlName="emergencyContactName">

      <label for="relationship">Relationship: </label>
      <input id="relationship" type="text" formControlName="relationship">

      <label for="emergency-contact-phone">Phone:</label>
      <input id="emergency-contact-phone" type="text" formControlName="emergencyContactPhone">

    </div>
  </div>
  


  <p>Complete the form to enable button.</p>
  <button type="submit" [disabled]="!profileForm.valid">Submit</button>
</form>
