import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Allergy } from '../models/Allergy.model';
import { HealthcareProvider } from '../models/HealthcareProvider.model';
import { ActivatedRoute } from '@angular/router';
import { Person } from '../models/Person.model';
import { Address } from '../models/Address.model';
import { EmergencyContact } from '../models/EmergencyContact.model';
import { Appointment } from '../models/Appointment.model';
import { Prescription } from '../models/Prescription.model';
import { ProfileFormService } from '../services/profile-form.service';
import { ToastrService } from 'ngx-toastr';

@Component({
  selector: 'app-patient-update',
  templateUrl: './patient-update.component.html',
  styleUrls: ['./patient-update.component.css']
})
export class PatientUpdateComponent implements OnInit {

  personId: string = "";
  person!: Person;
  address!: Address;
  emergencyContact!: EmergencyContact;
  appointments: Appointment[] = [];
  prescriptions: Prescription[] = [];
  allergies: Allergy[] = [];
  commonAllergies: Allergy[] = [];
  healthcareProviders: HealthcareProvider[] = [];

  constructor(
    private route: ActivatedRoute,
    private patientService: ProfileFormService,
    private toastrServise: ToastrService
  ) {}

  ngOnInit(): void {
    this.route.params.subscribe((params) => {
      this.personId = params['id'];
      this.loadData();
    });
  }

  loadData() {
      this.patientService.getPatientData(this.personId).subscribe((data) => {
      this.person = data.person;
      this.address = data.address;
      this.emergencyContact = data.emergencyContact;
      this.appointments = data.appointments;
      this.prescriptions = data.prescriptions;
      this.allergies = data.allergies;
      this.commonAllergies = data.commonAllergies;
      this.healthcareProviders = data.healthcareProviders;
   });
  }

  onSubmit(personalInfo: NgForm) {
    if(personalInfo.valid){
      this.patientService.editPerson(this.person? this.person: {} as Person)
    .subscribe({
      next: res => {
        console.log(res);
      },
      error: err => {
        console.log(err);
      }
    });
    this.patientService.editAddress(this.address? this.address: {} as Address)
    .subscribe({
      next: res => {
        console.log(res);
      },
      error: err => {
        console.log(err);
      }
    });
    this.patientService.editEmergencyContact(this.emergencyContact? this.emergencyContact: {} as EmergencyContact)
    .subscribe({
      next: res => {
        console.log(res);
      },
      error: err => {
        console.log(err);
      }
    });
    }    
  }
}
<form #personalInfo = ngForm (submit)="onSubmit(personalInfo)">
  <div class="row">
    <div class="col-md-4">
      <h2>Personal information</h2>
      <label for="first-name">First Name: </label>
      <input id="first-name" #firstName = "ngModel" type="text" name="firstName" 
      [(ngModel)] = "person.firstName" required>

      <label for="last-name">Last Name: </label>
      <input id="last-name" #lastName = "ngModel" type="text" name="lastName" 
      [(ngModel)] = "person.lastName" required>

      <label for="date-of-birth">Date of birth: </label>
      <input id="date-of-birth" #dateOfBirth = "ngModel" type="datetime" name="dateOfBirth" 
      [(ngModel)] = "person.dateOfBirth" required>

      <label for="gender">Gender: </label>
      <input id="gender" #gender = "ngModel" type="text" name="gender"
      [(ngModel)] = "person.gender">

      <label for="phone-number">Phone number: </label>
      <input id="phone-number" #phoneNumber = "ngModel" type="text" name="phoneNumber"
      [(ngModel)] = "person.phoneNumber" required>

      <label for="email">Email: </label>
      <input id="email" #email = "ngModel" type="email" name="email" 
      [(ngModel)] = "person.email" required>
    </div>

    <div class="col-md-4">
        <h2>Address</h2>

        <label for="street">Street: </label>
        <input id="street" #street = "ngModel" type="text" name="street" 
        [(ngModel)] = "address.street" required>

        <label for="city">City: </label>
        <input id="city" #city = "ngModel" type="text" name="city" 
        [(ngModel)] = "address.city" required>

        <label for="country">Country: </label>
        <input id="country" #country = "ngModel" type="text" name="country"
        [(ngModel)] = "address.country" required>

        <label for="zip-code">Zip Code: </label>
        <input id="zip-code" #zipCode = "ngModel" type="text" name="zipCode" 
        [(ngModel)] = "address.zipCode" required>     
    </div>

    <div class="col-md-4">
      <h2>Emergency contact</h2>

      <label for="name">Name:</label>
      <input id="name" #name = "ngModel" type="text" name="name" 
      [(ngModel)] = "emergencyContact.name" required>

      <label for="relationship">Relationship: </label>
      <input id="relationship" #relationship = "ngModel" type="text" name="relationship" 
      [(ngModel)] = "emergencyContact.relationship" required>

      <label for="phoneNumber">Phone:</label>
      <input id="phoneNumber" #phoneNumber = "ngModel" type="text" name="phoneNumber" 
      [(ngModel)] = "emergencyContact.phoneNumber" required>
    </div>
  </div>

  <button class="btn btn-success" type="submit">Submit</button>
</form>
